import { mapGetters, mapMutations } from 'vuex'

export default {
  data() {
    return {
      isShow: true
    }
  },
  computed: {
    secondMenuMap() {
      if (this.menuMap[this.moduleMenuIndex].children[0] && this.menuMap[this.moduleMenuIndex].children[this.firstMenuIndex].children) {
        return this.menuMap[this.moduleMenuIndex].children[this.firstMenuIndex].children
      } else {
        return []
      }
    },
    ...mapGetters([
      'menuMap',
      'themeColor',
      'moduleMenuIndex',
      'firstMenuIndex',
      'secondMenuIndex',
      'currentPath'
    ])
  },
  watch: {
    secondMenuMap(newVal, oldVal) {
      this.isShow = false
      setTimeout(() => {
        this.isShow = true
      }, 380)
    }
  },
  methods: {
    clickSecondMenu(secondMenu) {
      this.setSecondMenuIndex(secondMenu)
    },
    ...mapMutations({
      'setSecondMenuIndex': 'SET_SECOND_MENU_INDEX'
    })
  }
}
