.hamburger {
  cursor: pointer;
  width: 26px;
  height: 20px;
  .line {
    width: 100%;
    height: 3px;
    background-color: #333;
    transition: all .38s;
    -webkit-transition: all .38s;
  }
  &.desktop {
    .line {
      transform: rotate(0deg);
      opacity: 1;
    }
  }
  &.desktop-active {
    .line1 {
      transform: rotate(45deg) translate(6px, 6px);
    }
    .line2 {
      opacity: 0;
      transform: rotate(90deg);
    }
    .line3 {
      transform: rotate(-45deg) translate(6px, -6px);
    }
  }
  &.mobile {
    .line1 {
      transform: rotate(45deg) translate(6px, 6px);
    }
    .line2 {
      opacity: 0;
      transform: rotate(90deg);
    }
    .line3 {
      transform: rotate(-45deg) translate(6px, -6px);
    }
  }
  &.mobile-active {
    .line {
      transform: rotate(0deg);
      opacity: 1;
    }
  }
}
