result:
$bg: #fff;
$dark_gray: #24CBDA;
$light_gray: #3B3B4D;

.login-container {
  position: fixed;
  height: 100%;
  width: 100%;
  background-color: $bg;
  .login-form {
    max-width: 500px;
    padding: 35px 35px 15px 35px;
    margin: 20vh auto;
    background:$bg;
    box-shadow: 0 0 25px #cac6c6;
    border-radius:20px;
    .username {
      margin: 30px 0
    }
    .password {
      margin-bottom: 30px;
    }
    .btn-login {
      height: 45px;
      font-size:14px;
    }
  }
  .icon-container {
    padding: 6px 5px 6px 15px;
    color: $dark_gray;
    vertical-align: middle;
    width: 30px;
    display: inline-block;
    &_login {
      font-size: 20px;
    }
  }
  .title-container {
    position: relative;
    .title {
      font-size: 26px;
      color: $light_gray;
      margin: 0px auto 40px auto;
      text-align: center;
      font-weight: bold;
    }
    .set-language {
      color: #fff;
      position: absolute;
      top: 5px;
      right: 0px;
    }
  }
  .show-pwd {
    position: absolute;
    right: 10px;
    top: 7px;
    font-size: 16px;
    color: $dark_gray;
    cursor: pointer;
    user-select: none;
  }
  .thirdparty-button {
    position: absolute;
    right: 35px;
    bottom: 28px;
  }
}
#bg-canvas {
  position: absolute;
  z-index: -1;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
}
